import React, { useState, useMemo } from "react";
import LoanInsights from "./LoanInsights";
import axios from "axios";
import {
  Container,
  Grid,
  Card,
  CardContent,
  Typography,
  TextField,
  Button,
  Divider,
  Box,
  Chip,
  Stack,
  Tabs,
  Tab,
  CircularProgress,
  Switch,
  FormControlLabel,
  CssBaseline,
  ThemeProvider,
  createTheme,
} from "@mui/material";
import { Bar } from "react-chartjs-2";
import { Save, Calculate } from "@mui/icons-material";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { saveAs } from "file-saver";

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

function App() {
  const [principal, setPrincipal] = useState(3200000);
  const [emi, setEmi] = useState(31231);
  const [rate, setRate] = useState(8.35);
  const [tenure, setTenure] = useState(180);
  const [paidEmis, setPaidEmis] = useState(4);
  const [prepayments, setPrepayments] = useState([]);
  const [newPrepay, setNewPrepay] = useState({ month: "", amount: "" });
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [tab, setTab] = useState(0);
  const [darkMode, setDarkMode] = useState(false);

  const theme = useMemo(
    () =>
      createTheme({
        palette: {
          mode: darkMode ? "dark" : "light",
          primary: {
            main: darkMode ? "#90caf9" : "#1976d2",
          },
          background: {
            default: darkMode ? "#121212" : "#f9fafc",
            paper: darkMode ? "#1e1e1e" : "#fff",
          },
        },
        typography: {
          allVariants: {
            color: darkMode ? "#fff" : "#000",
          },
        },
      }),
    [darkMode]
  );

  const addPrepay = () => {
    if (!newPrepay.month || !newPrepay.amount) return;
    const sorted = [...prepayments, newPrepay].sort((a, b) => a.month - b.month);
    setPrepayments(sorted);
    setNewPrepay({ month: "", amount: "" });
  };

  const removePrepay = (index) => {
    const updated = prepayments.filter((_, i) => i !== index);
    setPrepayments(updated);
  };

  const calculate = async () => {
    try {
      setLoading(true);
      const res = await axios.post("https://loan-prepayment-calculator.onrender.com/api/reschedule", {
        principal,
        annualRate: rate,
        emi,
        totalTenure: tenure,
        paidEmis,
        prepayments,
      });
      setResult(res.data);
    } catch (err) {
      console.error("‚ùå API Error:", err);
      alert("Something went wrong. Please check your internet or backend.");
    } finally {
      setLoading(false);
    }
  };

  const chartData = useMemo(() => {
    if (!result) return null;
    const schedule = result.keepEMI?.schedule || [];
    return {
      labels: schedule.map((r) => `M${r.month}`),
      datasets: [
        {
          label: "Principal Component",
          data: schedule.map((r) => r.principal),
          backgroundColor: "rgba(75, 192, 192, 0.7)",
        },
        {
          label: "Interest Component",
          data: schedule.map((r) => r.interest),
          backgroundColor: "rgba(255, 99, 132, 0.7)",
        },
      ],
    };
  }, [result]);

  const exportCSV = () => {
    if (!result?.keepEMI?.schedule) return;
    const rows = result.keepEMI.schedule.map(
      (r) => `${r.month},${r.emi},${r.principal},${r.interest},${r.remaining}`
    );
    const csv = ["Month,EMI,Principal,Interest,Remaining", ...rows].join("\n");
    const blob = new Blob([csv], { type: "text/csv;charset=utf-8;" });
    saveAs(blob, "loan_schedule.csv");
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ py: 4, transition: "all 0.3s ease" }}>
        <Stack direction="row" justifyContent="space-between" alignItems="center" mb={2}>
          <Typography
            variant="h4"
            fontWeight={600}
            sx={{
              background:
                "linear-gradient(90deg, #0052D4 0%, #4364F7 50%, #6FB1FC 100%)",
              WebkitBackgroundClip: "text",
              WebkitTextFillColor: "transparent",
            }}
          >
            üè¶ Loan Prepayment ‚Äî Bank-Matching Calculator
          </Typography>

         <Stack direction="row" alignItems="center" spacing={2}>
  <Button
    variant="outlined"
    color="info"
    onClick={() =>
      window.open("https://economictimes.indiatimes.com/industry/banking/rssfeeds/13358259.cms", "_blank")
    }
  >
    üìä Loan Insights
  </Button>
  <FormControlLabel
    control={<Switch checked={darkMode} onChange={() => setDarkMode(!darkMode)} />}
    label="Dark Mode"
  />
</Stack>

        </Stack>

        <Grid container spacing={3}>
          {/* Loan Input Section */}
          <Grid item xs={12} md={4}>
            <Card sx={{ p: 2, boxShadow: 3, transition: "all 0.3s ease" }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>
                  Loan Basics
                </Typography>

                <Stack spacing={2}>
                  <TextField label="Principal (‚Çπ)" value={principal} onChange={(e) => setPrincipal(Number(e.target.value))} />
                  <TextField label="EMI (‚Çπ)" value={emi} onChange={(e) => setEmi(Number(e.target.value))} />
                  <TextField label="Rate p.a. (%)" value={rate} onChange={(e) => setRate(Number(e.target.value))} />
                  <TextField label="Tenure (months)" value={tenure} onChange={(e) => setTenure(Number(e.target.value))} />
                  <TextField label="EMIs Paid" value={paidEmis} onChange={(e) => setPaidEmis(Number(e.target.value))} />
                </Stack>

                <Divider sx={{ my: 2 }} />

                <Typography variant="h6">Prepayment Plan</Typography>
                <Stack spacing={2}>
                  <TextField label="After which EMI number" value={newPrepay.month} onChange={(e) => setNewPrepay({ ...newPrepay, month: e.target.value })} />
                  <TextField label="Amount (‚Çπ)" value={newPrepay.amount} onChange={(e) => setNewPrepay({ ...newPrepay, amount: e.target.value })} />
                  <Button variant="contained" onClick={addPrepay}>Add Prepayment</Button>
                </Stack>

                {prepayments.length > 0 && (
                  <Box mt={2}>
                    <Typography variant="subtitle1" gutterBottom>
                      Planned Prepayments:
                    </Typography>
                    <Stack direction="row" flexWrap="wrap" spacing={1}>
                      {prepayments.map((p, i) => (
                        <Chip
                          key={i}
                          label={`After EMI ${p.month} ‚Üí ‚Çπ${Number(p.amount).toLocaleString("en-IN")}`}
                          onDelete={() => removePrepay(i)}
                          color="primary"
                          variant="outlined"
                          sx={{ m: 0.5 }}
                        />
                      ))}
                    </Stack>
                  </Box>
                )}

                <Divider sx={{ my: 2 }} />
                <Stack direction="row" spacing={2} justifyContent="center">
                  <Button
                    variant="contained"
                    startIcon={<Calculate />}
                    onClick={calculate}
                    disabled={loading}
                  >
                    {loading ? <CircularProgress size={24} /> : "Calculate"}
                  </Button>
                  <Button variant="outlined" color="success" startIcon={<Save />} onClick={exportCSV}>
                    Export CSV
                  </Button>
                </Stack>
              </CardContent>
            </Card>
          </Grid>

          {/* Results Section */}
          <Grid item xs={12} md={8}>
            {result && (
              <Card sx={{ p: 3, boxShadow: 4, transition: "all 0.3s ease" }}>
                <Typography variant="h6" gutterBottom>
                  üìà Result Summary
                </Typography>

                <Typography>
                  <b>Outstanding:</b> ‚Çπ
                  {result.outstandingAfterPrepayments.toLocaleString("en-IN")}
                </Typography>
                <Typography sx={{ color: "#1976d2", mt: 1 }}>
                  <b>Keep EMI (Reduce Tenure):</b> {result.keepEMI.monthsToFinish} months | Interest: ‚Çπ
                  {result.keepEMI.totalInterest.toLocaleString("en-IN")}
                </Typography>
                <Typography sx={{ color: "#2e7d32", mt: 1 }}>
                  <b>Reduce EMI (Keep Tenure):</b> ‚Çπ
                  {result.reduceEMI.newEmi.toLocaleString("en-IN")} | Interest: ‚Çπ
                  {result.reduceEMI.totalInterest.toLocaleString("en-IN")}
                </Typography>

                <Tabs value={tab} onChange={(_, v) => setTab(v)} sx={{ mt: 3 }}>
                  <Tab label="Overview" />
                  <Tab label="Full Schedule" />
                </Tabs>

                {tab === 0 && (
                  <Box sx={{ mt: 2 }}>
                    <Bar
                      data={chartData}
                      options={{
                        plugins: {
                          legend: {
                            labels: { color: darkMode ? "#fff" : "#000" },
                          },
                          title: {
                            display: true,
                            text: "EMI Split (Principal vs Interest)",
                            color: darkMode ? "#fff" : "#000",
                          },
                        },
                        scales: {
                          x: { ticks: { color: darkMode ? "#fff" : "#000" } },
                          y: { ticks: { color: darkMode ? "#fff" : "#000" } },
                        },
                        responsive: true,
                        animation: { duration: 800, easing: "easeOutBounce" },
                      }}
                    />
                  </Box>
                )}

                {tab === 1 && (
  <Box sx={{ mt: 2, maxHeight: 400, overflow: "auto", fontSize: 14 }}>
    <table
      width="100%"
      border="1"
      style={{
        borderCollapse: "collapse",
        color: darkMode ? "#e0e0e0" : "#000",
        width: "100%",
        backgroundColor: darkMode ? "#1e1e1e" : "#fff",
      }}
    >
      <thead
        style={{
          backgroundColor: darkMode ? "#2a2a2a" : "#1976d2",
          color: darkMode ? "#90caf9" : "#fff",
          position: "sticky",
          top: 0,
          zIndex: 2,
        }}
      >
        <tr>
          <th>Month</th>
          <th>EMI</th>
          <th>Principal</th>
          <th>Interest</th>
          <th>Remaining</th>
        </tr>
      </thead>
      <tbody>
        {result.keepEMI.schedule.slice(0, 50).map((r, i) => (
          <tr
            key={i}
            style={{
              backgroundColor: darkMode
                ? i % 2 === 0
                  ? "#212121"
                  : "#181818"
                : i % 2 === 0
                ? "#f5f5f5"
                : "#ffffff",
            }}
          >
            <td>{r.month}</td>
            <td>{r.emi}</td>
            <td>{r.principal}</td>
            <td>{r.interest}</td>
            <td>{r.remaining}</td>
          </tr>
        ))}
      </tbody>
    </table>
  </Box>
)}

              </Card>
            )}
            <LoanInsights darkMode={darkMode} />
          </Grid>
        </Grid>
      </Container>
    </ThemeProvider>
  );
}

export default App;
