import React, { useState } from 'react';
import axios from 'axios';
import { Line } from 'react-chartjs-2';
import { saveAs } from 'file-saver';
import { Chart, registerables } from 'chart.js';
Chart.register(...registerables);

const SERVER = 'http://localhost:4000';

function App() {
  // defaults from your GIC data
  const [principal, setPrincipal] = useState(3200000);
  const [emi, setEmi] = useState(31231);
  const [annualRate, setAnnualRate] = useState(8.5);
  const [tenure, setTenure] = useState(180);
  const [paidEmis, setPaidEmis] = useState(4);
  const [prepayments, setPrepayments] = useState([]); // {month,amount}
  const [monthInput, setMonthInput] = useState(4);
  const [amountInput, setAmountInput] = useState(110000);
  const [mode, setMode] = useState('reduce_tenure');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  function addPrepay() {
    if (!amountInput || amountInput <= 0) return;
    setPrepayments([...prepayments, { month: Number(monthInput), amount: Number(amountInput) }]);
  }
  function clearPrepay() { setPrepayments([]); }

  async function calculate() {
    setLoading(true);
    try {
      const resp = await axios.post(`${SERVER}/api/reschedule`, {
        officialSchedule: null, // you can later add official schedule upload
        principal,
        annualRate,
        emi,
        totalTenure: tenure,
        paidEmis,
        prepayments,
        mode
      });
      setResult(resp.data);
    } catch (e) {
      alert('Error: ' + (e.response?.data?.error || e.message));
    }
    setLoading(false);
  }

  function exportCSV() {
    if (!result) return;
    const rows = (mode === 'reduce_tenure' ? result.keepEMI.schedule : result.reduceEMI.remainingSchedule);
    if (!rows) return;
    const header = 'Month,EMI_paid,Principal,Interest,Remaining\n';
    const body = rows.map(r => `${r.month},${r.emi_paid || (mode!=='reduce_tenure' ? r.emi_paid : '')},${r.principal},${r.interest},${r.remaining}`).join('\n');
    const blob = new Blob([header + body], { type: 'text/csv;charset=utf-8;' });
    saveAs(blob, 'revised_amortization.csv');
  }

  const chartData = result ? {
    labels: (result.keepEMI.schedule || result.reduceEMI.remainingSchedule || []).map(r => 'M' + r.month),
    datasets: [
      {
        label: 'Remaining Balance',
        data: (result.keepEMI.schedule || result.reduceEMI.remainingSchedule || []).map(r => r.remaining),
        fill: false,
        tension: 0.2
      }
    ]
  } : null;

  return (
    <div style={{ padding: 20, maxWidth: 1200, margin: '0 auto' }}>
      <h1>Loan Prepayment — Bank-matching calculator</h1>
      <section style={{display:'flex', gap:20}}>
        <div style={{flex:1}}>
          <h3>Loan basics</h3>
          <label>Principal: <input type="number" value={principal} onChange={e=>setPrincipal(Number(e.target.value))} /></label><br/>
          <label>EMI: <input type="number" value={emi} onChange={e=>setEmi(Number(e.target.value))} /></label><br/>
          <label>Rate p.a.: <input type="number" value={annualRate} onChange={e=>setAnnualRate(Number(e.target.value))} step="0.1" /></label><br/>
          <label>Tenure (months): <input type="number" value={tenure} onChange={e=>setTenure(Number(e.target.value))} /></label><br/>
          <label>EMIs paid: <input type="number" value={paidEmis} onChange={e=>setPaidEmis(Number(e.target.value))} /></label>
          <hr/>
          <h3>Add a prepayment</h3>
          <label>After which EMI number: <input type="number" value={monthInput} onChange={e=>setMonthInput(e.target.value)} /></label><br/>
          <label>Amount (₹): <input type="number" value={amountInput} onChange={e=>setAmountInput(e.target.value)} /></label><br/>
          <button onClick={addPrepay}>Add Prepayment</button>
          <button onClick={clearPrepay} style={{marginLeft:10}}>Clear</button>
          <div>
            <h4>Planned prepayments</h4>
            <ul>
              {prepayments.map((p,i)=><li key={i}>After EMI {p.month} → ₹{p.amount}</li>)}
            </ul>
          </div>
          <div>
            <label>
              <input type="radio" name="mode" checked={mode==='reduce_tenure'} onChange={()=>setMode('reduce_tenure')} />
              Reduce Tenure (keep EMI)
            </label>
            <label style={{marginLeft:10}}>
              <input type="radio" name="mode" checked={mode==='reduce_emi'} onChange={()=>setMode('reduce_emi')} />
              Reduce EMI (keep tenure)
            </label>
          </div>
          <div style={{marginTop:10}}>
            <button onClick={calculate} disabled={loading}>{loading ? 'Calculating...' : 'Calculate'}</button>
            <button onClick={exportCSV} disabled={!result} style={{marginLeft:10}}>Export CSV (revised)</button>
          </div>
        </div>

        <div style={{flex:1.6}}>
          <h3>Result</h3>
          {!result && <div>No calculation yet</div>}
          {result && <>
            <div>
              <strong>Outstanding after prepayments:</strong> ₹{result.outstandingAfterPrepayments.toLocaleString()}
            </div>
            <div style={{marginTop:8}}>
              <strong>Keep EMI (Reduce Tenure)</strong> — Months to finish: {result.keepEMI.monthsToFinish}, interest to pay: ₹{result.keepEMI.totalInterest}
              <br/>
              <strong>Reduce EMI (Keep tenure)</strong> — New EMI: ₹{result.reduceEMI.newEmi}, interest to pay: ₹{result.reduceEMI.totalInterest}
            </div>

            <div style={{marginTop:12}}>
              <h4>Simulation Log (EMI & Prepay actions up to last prepay month)</h4>
              <div style={{maxHeight:160, overflow:'auto', border:'1px solid #ddd', padding:8}}>
                <pre style={{whiteSpace:'pre-wrap'}}>{JSON.stringify(result.simLog, null, 2)}</pre>
              </div>
            </div>

            <div style={{marginTop:12}}>
              <h4>Amortization preview</h4>
              <div style={{maxHeight:200, overflow:'auto', border:'1px solid #ddd'}}>
                <table style={{width:'100%', borderCollapse:'collapse'}}>
                  <thead>
                    <tr><th>Month</th><th>EMI</th><th>Principal</th><th>Interest</th><th>Remaining</th></tr>
                  </thead>
                  <tbody>
                    {(mode==='reduce_tenure' ? result.keepEMI.schedule : result.reduceEMI.remainingSchedule).slice(0,40).map(r=>(
                      <tr key={r.month}>
                        <td>{r.month}</td>
                        <td>{r.emi_paid || '-'}</td>
                        <td>{r.principal}</td>
                        <td>{r.interest}</td>
                        <td>{r.remaining}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>

            <div style={{marginTop:12}}>
              {chartData && <Line data={chartData} />}
            </div>
          </>}
        </div>
      </section>
    </div>
  );
}

export default App;
